draw(Circle((-2,0),1));
draw(Circle((2,0),1));
draw(Circle((-1,1.73205081),1));
draw(Circle((1,1.73205081),1));
draw(Circle((-1,-1.73205081),1));
draw(Circle((1,-1.73205081),1));
