pair A, B, P, R, S, T;
pair[] O;
real[] r;

r[1] = 1;
r[2] = 0.8;

S = (-2*r[1],0);
O[1] = (-r[1],0);
R = (0,0);
O[2] = (r[2],0);
T = (2*r[2],0);
A = O[1] + dir(180 - 58)*r[1];
B = O[2] + dir(37)*r[2];
P = extension(A, A + rotate(90)*(A - O[1]), B, B + rotate(90)*(B - O[2]));

draw(S--T);
draw(arc(O[1],r[1],0,180));
draw(arc(O[2],r[2],0,180));
draw(A--P--B);
draw(A--O[1]);
draw(B--O[2]);

