draw((0,0)--(4,0)--(3,5)--cycle);
draw(incircle((0,0),(4,0),(3,5)));
real x = 1.15;
pair A = (0,0) + x*(-3,-5);
pair B = (4,0) + x*(1,-5);
draw(A--(3,5)--B--cycle);
draw(incircle(A,(3,5),B));
label("$A$",(3,5),N);
label("$B$",(4,0),E);
label("$C$",(0,0),W);
