draw((0,-3)--(0,3));

for (i = -20; i <= 20; ++i) {
for (j = -20; j <= 20; ++j) {
  Z = (i,0) + j*dir(120);
	if (abs(Z.x) <= 3.1 && abs(Z.y) <= 3.1) {dot(Z);}
}}
[/asy]
