\[\frac{(-11 + 3i) + (3 - 7i)}{2} = \boxed{-7 - 3i}.\][asy]
unitsize(0.4 cm);

pair A, B, M;

A = (-11,3);
B = (3,-7);
M = (A + B)/2;

draw(A--B);

dot("$-1 + 3i$", A ,NW);
dot("$31 - 4i$", B, SE);
dot("$-7 - 3i$", M, NE);
