pair A,B,C,D;
B = (0,0);
C = (1,0);
A = rotate(135)*(0.6,0);
D = A+C;
draw(A--B--C--D--A);
label("$A$",A,N);
label("$D$",D,N);
label("$B$",B,S);
label("$C$",C,S);
[/asy]

