B = (0,1,0);
C = (-1,0,0);
D = (0,-1,0);
E = (0,0,1);
Ap = (1,0,1);
Bp = (0,1,1);
Cp = (-1,0,1);
Dp = (0,-1,1);
Ep = (0,0,0);
M = (A + E)/2;
N = (B + E)/2;
P = (C + E)/2;
Q = (D + E)/2;

draw(D--A--B);
draw(D--C--B,dashed);
draw(C--E,dashed);
draw(A--M);
draw(M--E,dashed);
draw(B--N);
draw(N--E,dashed);
draw(D--Q);
draw(Q--E,dashed);
draw(Ap--Bp--Cp--Dp--cycle);
draw(Ap--M);
draw(M--Ep,dashed);
draw(Bp--N);
draw(N--Ep,dashed);
draw(Cp--Ep,dashed);
draw(Dp--Q);
draw(Q--Ep,dashed);
draw(Q--M--N);
draw(Q--P--N,dashed);

label("$A$", A, SW);
label("$B$", B, dir(0));
label("$C$", C, S);
label("$D$", D, W);
label("$E$", E, dir(90));
label("$A'$", Ap, dir(90));
label("$B'$", Bp, dir(0));
label("$C'$", Cp, dir(90));
