A = (-5,1);
B = (2,3);
O = (0,0);
P = (O + reflect(A,B)*(O))/2;

draw(O--A,Arrow(6));
draw(O--B,Arrow(6));
draw(O--P,Arrow(6));
draw(interp(A,B,-0.1)--interp(A,B,1.1),dashed);

label("$\begin{pmatrix} 1 \\ -1 \\ 2 \end{pmatrix}$", A, N);
