label("$(0,0,4)$", B, N);
label("$(0,4,0)$", C, dir(0));
label("$(0,4,4)$", D, NE);
label("$(4,0,0)$", E, W);
label("$(4,0,4)$", F, W);
label("$(4,4,0)$", G, dir(270));
label("$(4,4,4)$", H, SW);
dot("$P$", P, dir(270));
dot("$Q$", Q, dir(270));
dot("$R$", R, N);
dot("$S$", S, NW);
dot("$T$", T, dir(0));
[/asy]

