A = ((-5 - 4)/(5/2),-5);
B = ((5 - 4)/(5/2),5);
P = ((0,0) + reflect(A,B)*((0,0)))/2;
V = (-2, 5/2*(-2) + 4);

draw((-5,0)--(5,0));
draw((0,-5)--(0,5));
draw(A--B,red);
draw((0,0)--P,Arrow(6));
draw((0,0)--V,Arrow(6));

label("$\mathbf{p}$", P, W);
label("$\mathbf{v}$", V, W);
[/asy]
#### \begin{pmatrix} -40/29 \\ 16/29 \end{pmatrix}

